import React, { useEffect, useState } from "react";
import api from "../services/api";
import "../styles/Admin.css";

const UserAnalyticsPage = () => {
  const [analytics, setAnalytics] = useState(null);
  const [userSummary, setUserSummary] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetch = async () => {
      try {
        setLoading(true);
        const [plat, user] = await Promise.all([
          api.get('/analytics/summary'),
          api.get('/analytics/user'),
        ]);
        setAnalytics(plat.data);
        setUserSummary(user.data);
      } catch (e) {
        console.error('Failed to load analytics', e);
      } finally {
        setLoading(false);
      }
    };
    fetch();
  }, []);

  if (loading) return <div className="admin-loading">Loading analytics...</div>;

  const reported = userSummary?.reported ?? 0;
  const recovered = userSummary?.recovered ?? 0;
  const notRecovered = userSummary?.notRecovered ?? 0;

  // pie slices for user engagement
  const slices = [
    { label: 'Recovered', value: recovered, color: '#27ae60' },
    { label: 'Not Recovered', value: notRecovered, color: '#e74c3c' },
  ];
  const sum = slices.reduce((s, it) => s + Math.max(0, it.value), 0) || 1;
  let acc = 0;
  const paths = slices.map((s) => {
    const start = acc / sum;
    acc += Math.max(0, s.value);
    const end = acc / sum;
    return { ...s, start, end };
  });

  const cx = 100, cy = 100, r = 80;
  const polar = (t) => {
    const angle = 2 * Math.PI * t - Math.PI / 2;
    return { x: cx + r * Math.cos(angle), y: cy + r * Math.sin(angle) };
  };
  const arcPath = (start, end) => {
    const p1 = polar(start);
    const p2 = polar(end);
    const large = end - start > 0.5 ? 1 : 0;
    return `M ${cx} ${cy} L ${p1.x} ${p1.y} A ${r} ${r} 0 ${large} 1 ${p2.x} ${p2.y} Z`;
  };

  return (
    <div className="section-wrapper">
      <div className="analytics-section">
        <h2>Platform Analytics</h2>
        {analytics ? (
          <div className="analytics-grid">
            <div className="stat-card">
              <div className="stat-value">{analytics.total || 0}</div>
              <div className="stat-label">Total Items</div>
            </div>
            <div className="stat-card">
              <div className="stat-value" style={{ color: "#e74c3c" }}>{analytics.open || 0}</div>
              <div className="stat-label">Open</div>
            </div>
            <div className="stat-card">
              <div className="stat-value" style={{ color: "#27ae60" }}>{analytics.matched || 0}</div>
              <div className="stat-label">Matched</div>
            </div>
            <div className="stat-card">
              <div className="stat-value" style={{ color: "#95a5a6" }}>{analytics.closed || 0}</div>
              <div className="stat-label">Closed</div>
            </div>
          </div>
        ) : (
          <p className="no-data">No analytics available</p>
        )}
      </div>

      <div className="engagement-section">
        <h2>Your Engagement</h2>
        <div className="engagement-card">
          <div className="engagement-pie-container">
            <svg viewBox="0 0 200 200" width="220" height="220" aria-hidden>
              {paths.map((p, idx) => (
                <path key={idx} d={arcPath(p.start, p.end)} fill={p.color} stroke="#fff" strokeWidth="1" />
              ))}
              <circle cx={cx} cy={cy} r={r - 40} fill="#ffffff" />
              <text x={cx} y={cy - 6} textAnchor="middle" fontSize="14" fontWeight="700" fill="#2c3e50">{reported}</text>
              <text x={cx} y={cy + 14} textAnchor="middle" fontSize="12" fill="#95a5a6">Your Reports</text>
            </svg>

            <div className="engagement-legend">
              {paths.map((p, i) => (
                <div className="legend-row" key={i}>
                  <span className="legend-color" style={{ background: p.color }} />
                  <span className="legend-label">{p.label}</span>
                  <span className="legend-value">{p.value}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserAnalyticsPage;
