import React, { useEffect, useState } from "react";
import api from "../services/api";
import "../styles/Admin.css";

const AdminDashboardPage = () => {
  const [items, setItems] = useState([]);
  const [analytics, setAnalytics] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [deleteConfirm, setDeleteConfirm] = useState(null);
  const [editingItem, setEditingItem] = useState(null);
  const [editFormData, setEditFormData] = useState({});
  const [editLoading, setEditLoading] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      const [itemsRes, analyticsRes] = await Promise.all([
        api.get("/admin/items"),
        api.get("/analytics/summary"),
      ]);
      setItems(itemsRes.data);
      setAnalytics(analyticsRes.data);
      setError(null);
    } catch (err) {
      setError("Failed to load admin data. Please check your permissions.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const deleteItem = async (id) => {
    try {
      await api.delete(`/admin/items/${id}`);
      setItems(items.filter((i) => i.id !== id));
      setDeleteConfirm(null);
    } catch (err) {
      setError("Failed to delete item. Please try again.");
      console.error(err);
    }
  };

  const flagItem = async (id) => {
    try {
      await api.post(`/items/${id}/flag`);
      alert("Item flagged successfully");
    } catch (err) {
      setError("Failed to flag item. Please try again.");
      console.error(err);
    }
  };

  const openEditDialog = (item) => {
    setEditingItem(item);
    setEditFormData({
      title: item.title || "",
      description: item.description || "",
      location: item.location || "",
      category: item.category || "",
      status: item.status || "OPEN",
      matched: item.matched || false,
    });
  };

  const closeEditDialog = () => {
    setEditingItem(null);
    setEditFormData({});
  };

  const handleEditFormChange = (e) => {
    const { name, value, type, checked } = e.target;
    setEditFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const saveEditedItem = async () => {
    if (!editingItem) return;

    try {
      setEditLoading(true);
      setError(null);
      console.log("Sending PUT request to /api/items/" + editingItem.id, editFormData);

      const response = await api.put(`/items/${editingItem.id}`, editFormData);
      console.log("Update response:", response.data);

      // Update the items list with the updated item
      setItems(items.map((item) =>
        item.id === editingItem.id ? response.data : item
      ));

      closeEditDialog();
      alert("Item updated successfully!");
    } catch (err) {
      console.error("Error updating item:", err);
      const errorMsg = err.response?.data?.message || 
                       err.response?.data?.error || 
                       err.message || 
                       "Failed to update item. Please try again.";
      setError(errorMsg);
      alert("Failed to update item: " + errorMsg);
    } finally {
      setEditLoading(false);
    }
  };

  if (loading) {
    return <div className="admin-loading">Loading admin dashboard...</div>;
  }

  return (
    <div className="admin-container">
      <div className="admin-header">
        <h1>Admin Dashboard</h1>
        <button onClick={fetchData} className="refresh-btn">
          Refresh Data
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      {/* Analytics Section */}
      <div className="analytics-section">
        <h2>Platform Analytics</h2>
        {analytics ? (
          <div className="analytics-grid">
            <div className="stat-card">
              <div className="stat-value">{analytics.total || 0}</div>
              <div className="stat-label">Total Items</div>
            </div>
            <div className="stat-card">
              <div className="stat-value" style={{ color: "#e74c3c" }}>
                {analytics.open || 0}
              </div>
              <div className="stat-label">Open</div>
            </div>
            <div className="stat-card">
              <div className="stat-value" style={{ color: "#27ae60" }}>
                {analytics.matched || 0}
              </div>
              <div className="stat-label">Matched</div>
            </div>
            <div className="stat-card">
              <div className="stat-value" style={{ color: "#95a5a6" }}>
                {analytics.closed || 0}
              </div>
              <div className="stat-label">Closed</div>
            </div>
          </div>
        ) : (
          <p className="no-data">No analytics data available</p>
        )}
      </div>

      {/* Items Management Section */}
      <div className="items-section">
        <h2>Manage Items ({items.length})</h2>
        {items.length === 0 ? (
          <p className="no-data">No items found</p>
        ) : (
          <div className="table-responsive">
            <table className="admin-table">
              <thead>
                <tr>
                  <th>ID</th>
                  <th>Title</th>
                  <th>Type</th>
                  <th>Category</th>
                  <th>Status</th>
                  <th>Matched</th>
                  <th>Posted By</th>
                  <th>Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {items.map((item) => (
                  <tr key={item.id} className={`item-row status-${item.status?.toLowerCase()}`}>
                    <td className="id-cell">{item.id}</td>
                    <td className="title-cell">
                      <div className="title-content">{item.title}</div>
                      <div className="description">
                        {item.description?.substring(0, 50)}
                        {item.description?.length > 50 ? "..." : ""}
                      </div>
                    </td>
                    <td>
                      <span
                        className={`type-badge ${item.type?.toLowerCase()}`}
                      >
                        {item.type}
                      </span>
                    </td>
                    <td>{item.category || "N/A"}</td>
                    <td>
                      <span
                        className={`status-badge status-${item.status?.toLowerCase()}`}
                      >
                        {item.status}
                      </span>
                    </td>
                    <td>
                      <span
                        className={`matched-badge ${item.matched ? "matched-yes" : "matched-no"}`}
                      >
                        {item.matched ? "??? Yes" : "??? No"}
                      </span>
                    </td>
                    <td className="user-cell">
                      {item.postedBy?.name}
                      <div className="user-email">{item.postedBy?.email}</div>
                    </td>
                    <td className="date-cell">
                      {new Date(item.createdAt).toLocaleDateString([], {
                        month: "short",
                        day: "numeric",
                        year: "numeric",
                      })}
                    </td>
                    <td className="actions-cell">
                      <button
                        className="action-btn edit-btn"
                        onClick={() => openEditDialog(item)}
                        title="Edit item"
                      >
                        ?????? Edit
                      </button>
                      <button
                        className="action-btn flag-btn"
                        onClick={() => flagItem(item.id)}
                        title="Flag item"
                      >
                        ???? Flag
                      </button>
                      <button
                        className="action-btn delete-btn"
                        onClick={() => setDeleteConfirm(item.id)}
                        title="Delete item"
                      >
                        ??????? Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Delete Confirmation Modal */}
      {deleteConfirm && (
        <div className="modal-overlay" onClick={() => setDeleteConfirm(null)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Confirm Delete</h3>
            <p>
              Are you sure you want to delete this item? This action cannot be
              undone.
            </p>
            <div className="modal-actions">
              <button
                className="btn-cancel"
                onClick={() => setDeleteConfirm(null)}
              >
                Cancel
              </button>
              <button
                className="btn-delete"
                onClick={() => deleteItem(deleteConfirm)}
              >
                Delete Item
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Item Modal */}
      {editingItem && (
        <div className="modal-overlay" onClick={closeEditDialog}>
          <div className="modal-content edit-modal" onClick={(e) => e.stopPropagation()}>
            <h3>Edit Item Details</h3>

            <div className="form-group">
              <label htmlFor="edit-title">Title</label>
              <input
                id="edit-title"
                type="text"
                name="title"
                value={editFormData.title}
                onChange={handleEditFormChange}
                placeholder="Item title"
              />
            </div>

            <div className="form-group">
              <label htmlFor="edit-description">Description</label>
              <textarea
                id="edit-description"
                name="description"
                value={editFormData.description}
                onChange={handleEditFormChange}
                placeholder="Item description"
                rows="4"
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="edit-location">Location</label>
                <input
                  id="edit-location"
                  type="text"
                  name="location"
                  value={editFormData.location}
                  onChange={handleEditFormChange}
                  placeholder="Item location"
                />
              </div>

              <div className="form-group">
                <label htmlFor="edit-category">Category</label>
                <input
                  id="edit-category"
                  type="text"
                  name="category"
                  value={editFormData.category}
                  onChange={handleEditFormChange}
                  placeholder="Item category"
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label htmlFor="edit-status">Status</label>
                <select
                  id="edit-status"
                  name="status"
                  value={editFormData.status}
                  onChange={handleEditFormChange}
                >
                  <option value="OPEN">OPEN</option>
                  <option value="CLOSED">CLOSED</option>
                </select>
              </div>

              <div className="form-group checkbox-group">
                <label htmlFor="edit-matched">
                  <input
                    id="edit-matched"
                    type="checkbox"
                    name="matched"
                    checked={editFormData.matched}
                    onChange={handleEditFormChange}
                  />
                  <span>Mark as Matched</span>
                </label>
              </div>
            </div>

            <div className="modal-actions">
              <button
                className="btn-cancel"
                onClick={closeEditDialog}
                disabled={editLoading}
              >
                Cancel
              </button>
              <button
                className="btn-save"
                onClick={saveEditedItem}
                disabled={editLoading}
              >
                {editLoading ? "Saving..." : "Save Changes"}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboardPage;
